import React, { useRef, useState } from "react"
import { Form, Button, Card, Alert } from "react-bootstrap"
import { useAuth } from "../contexts/AuthContext"
import { Link, useHistory } from "react-router-dom"
import './Login.css'

export default function Login() {
  const emailRef = useRef()
  const passwordRef = useRef()
  const { login } = useAuth()
  const [error, setError] = useState("")
  const [loading, setLoading] = useState(false)
  const history = useHistory()

  async function handleSubmit(e) {
    e.preventDefault()

    try {
      setError("")
      setLoading(true)
      await login(emailRef.current.value, passwordRef.current.value)
      history.push("/")
    } catch {
      setError("Failed to log in")
    }

    setLoading(false)
  }

  return (
    <div className="LoginPage">
      <div className="LoginContainer">
          <div className="LoginImage">  
          </div>
            {/* <Card className="LoginCard">
              <Card.Body> */}
                <h2 className="text-center mb-4">Login to AchieVas Academy</h2>
                {error && <Alert variant="danger">{error}</Alert>}
                <Form onSubmit={handleSubmit} className="formMain">
                  <Form.Group id="email">
                    <Form.Label className="heading">Email</Form.Label>
                    <Form.Control className="formBox" type="email" ref={emailRef} required />
                  </Form.Group>
                  <Form.Group id="password">
                    <Form.Label className="heading">Password</Form.Label>
                    <Form.Control className="formBox" type="password" ref={passwordRef} required />
                  </Form.Group>
                  <Button disabled={loading} className="w-100" className="btn" type="submit">
                    Login
                  </Button>
                </Form>
                <div className="w-100 text-center mt-3">
                  <Link to="/forgot-password">Forgot Password?</Link>
                </div>
                <div className="w-100 text-center mt-2">
                  Need an account? <Link to="/signup">Sign Up</Link>
                </div>
              {/* </Card.Body>
            </Card> */}

      </div>
    </div>
  )
}
